  @Test(expected = DataSetNotExistsException.class)
  public void shouldThrowDataSetNotExistsForUpdateDescription()
      throws MCSException {
    String providerId = "Provider002";
    String dataSetId = "noSuchDataset";
    String description = "TEST4";

    //
    wireMockRule.stubFor(put(urlEqualTo("/mcs/data-providers/" + providerId + "/data-sets/noSuchDataset"))
        .withHeader("Content-Type", matching("application/x-www-form-urlencoded"))
        .withRequestBody(matching("description=TEST4"))
        .willReturn(aResponse()
            .withStatus(404)
            .withHeader("Content-Type", "application/xml")
            .withBody(
                "<?xml version=\"1.0\" encoding=\"UTF-8\" standalone=\"yes\"?><errorInfo><details>Provider Provider002 does not have data set with id noSuchDataset</details><errorCode>DATASET_NOT_EXISTS</errorCode></errorInfo>")));
    //

    DataSetServiceClient instance = new DataSetServiceClient(baseUrl);
    instance.updateDescriptionOfDataSet(providerId, dataSetId, description);
  }

